const fechasYTextosString = msg.payload;

function separarFechasYTextos(fechasYTextosString) {
    const fechasYTextos = fechasYTextosString.split(',');

    // Obtener las fechas y darles formato
    const fechasSeparadas = fechasYTextos.slice(0, 2).map((fecha) => {
        const [dia, mes, anio] = fecha.split('/');
        return `${dia}/${mes}/${anio}`;
    });

    // Obtener los textos que siguen después de las fechas
    const textos = fechasYTextos.slice(2).map(item => item.trim());

    // Devolver un array que contiene las fechas y los textos
    return [...fechasSeparadas, ...textos];
}

